// Fonction pour obtenir les questions par catégorie
export function getQuestionsByCategory(categoryId: string): Question[] {
  return QUESTIONS_DB.filter(q => q.categoryId === categoryId);
}

// Fonction pour obtenir des questions aléatoires
export function getRandomQuestions(categoryId: string, count: number = 10): Question[] {
  const categoryQuestions = getQuestionsByCategory(categoryId);
  const shuffled = [...categoryQuestions].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, Math.min(count, shuffled.length));
}

// Fonction pour obtenir toutes les catégories disponibles
export function getAvailableCategories(): string[] {
  const categories = new Set(QUESTIONS_DB.map(q => q.categoryId));
  return Array.from(categories);
}

// Statistiques
export function getCategoryStats() {
  const stats: Record<string, number> = {};
  QUESTIONS_DB.forEach(q => {
    stats[q.categoryId] = (stats[q.categoryId] || 0) + 1;
  });
  return stats;
}
